using UnityEngine;
using UnityEngine.Networking;

using System;
using System.Text;
using System.Collections;
using System.Collections.Generic;
using System.Security.Cryptography.X509Certificates;

public class SHCustomCertificateHandler : CertificateHandler
{
    private static readonly string CERT_KEY = "";

    protected override bool ValidateCertificate(byte[] certificateData)
    {
        return CERT_KEY.Equals(System.Convert.ToBase64String(certificateData));
    }
}
